frontend.src/store/navigation.js:
const initialState = { loggedIn: false, currentPage: 'Home' };

const navigationReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOGIN':
      return { ...state, loggedIn: true };
    case 'LOGOUT':
      return { ...state, loggedIn: false };
    case 'CHANGE_PAGE':
      return { ...state, currentPage: action.payload };
    default:
      return state;
  }
};

export default navigationReducer;

frontend.src/store/modal.js:
const initialState = { isOpen: false, type: '', content: '' };

const modalReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'OPEN_MODAL':
      return { ...state, isOpen: true, type: action.payload.type, content: action.payload.content };
    case 'CLOSE_MODAL':
      return { ...state, isOpen: false };
    default:
      return state;
  }
};

export default modalReducer;

frontend.src/store/uiComponents.js:
const initialState = {
  accordionOpenIndex: null,
  tabsActiveTab: 0
};

const uiComponentsReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'TOGGLE_ACCORDION':
      return { ...state, accordionOpenIndex: action.payload };
    case 'SET_TABS_ACTIVE_TAB':
      return { ...state, tabsActiveTab: action.payload };
    default:
      return state;
  }
};

export default uiComponentsReducer;

